mapper.select:
SELECT
			 COUNT(APPR_NO)
	FROM T_APPROVAL
 WHERE APPR_USER = #{userNo}

 		AND APPR_STATUS = #{status}

SELECT APPR_NO , APPR_USER , APPR_TYPE , APPR_TITLE , DEPT_NO , APPR_DATE , APPR_STATUS , CREATE_DATE , MODIFY_DATE , UPDATE_USER
FROM T_APPROVAL WHERE APPR_USER = #{userNo}
AND APPR_STATUS = #{status}
ORDER BY APPR_DATE DESC

SELECT A.APPR_NO as APPR_NO , APPR_USER , DEPT_NO , APPR_TYPE , APPR_DATE , APPR_TITLE , APPR_CONTENT , APPR_STATUS , LINE_NO , USER_NO , LINE_ORDER , LINE_STATUS , LINE_DATE , LINE_REASON FROM T_APPROVAL A LEFT JOIN T_APPR_LINE L ON A.APPR_NO = L.APPR_NO WHERE USER_NO = #{userNo}
AND APPR_STATUS = #{status}
AND APPR_STATUS IN(2,3)

mapper.insert:
INSERT INTO T_APPR_LINE ( LINE_NO , APPR_NO , USER_NO , LINE_ORDER , LINE_STATUS , CREATE_DATE , CREATE_USER
, MODIFY_DATE , MODIFY_USER
) VALUES ( SEQ_ALNO.NEXTVAL ,
#{apprNo}
LPAD(SEQ_ANO.CURRVAL, 4, '0')
, #{userNo} , #{lineOrder} , 0 , SYSDATE , #{createUser}
, SYSDATE , #{modifyUser}
)

   INSERT 
     INTO T_DRAFT 
   (
       APPR_NO
     , ENFORCE_DATE
     , COOP_DEPT
   ) 
   VALUES 
   (


	       		#{apprNo}


			       LPAD(SEQ_ANO.CURRVAL, 4, '0')	       	


     , #{enforceDate}
