     , #{productNo}
     , #{productName}
     , #{productUnit}
     , #{productAmt}
     , #{productPrice}
   )

mapper.select:
SELECT COUNT(LINE_NO)
  FROM T_APPR_LINE
 WHERE APPR_NO = #{apprNo}
   AND LINE_ORDER > (
       SELECT LINE_ORDER 
         FROM T_APPR_LINE 
        WHERE APPR_NO = #{apprNo} 
          AND USER_NO = #{userNo}
   )

mapper.update:
UPDATE T_APPROVAL
   SET CURR_ORDER = CURR_ORDER + 1
 WHERE APPR_NO = #{apprNo}

UPDATE T_APPR_LINE
   SET LINE_STATUS = 1  
     , LINE_DATE = SYSDATE      
 WHERE APPR_NO = #{apprNo}
   AND USER_NO = #{userNo}

UPDATE T_APPR_LINE SET LINE_STATUS = 2 , LINE_DATE = SYSDATE
, LINE_REASON = #{lineReason}
WHERE APPR_NO = #{apprNo}

UPDATE T_APPROVAL
   SET APPR_STATUS = 2  
 WHERE APPR_NO = #{apprNo}

UPDATE T_APPROVAL
   SET APPR_STATUS = 3  
 WHERE APPR_NO = #{apprNo}

mapper.insert:
		INSERT 
		  INTO T_APPROVAL
			(
        APPR_NO,
        APPR_TYPE,
        APPR_CONTENT,
        APPR_STATUS,
        CREATE_DATE,
        CREATE_USER				
			)
		VALUES
		  (
		  	LPAD(SEQ_ANO.NEXTVAL,4,'0')
		  , #{apprType}
		  , #{apprContent}
		  , #{apprStatus}
		  , SYSDATE
		  , #{createUser}

		  )

mapper.update:
UPDATE T_APPROVAL 
   SET 
    	 APPR_TYPE = #{apprType}
     , APPR_CONTENT = #{apprContent}
     , MODIFY_DATE = SYSDATE
     , UPDATE_USER = #{updateUser}
 WHERE APPR_NO = #{apprNo}
   AND APPR_STATUS = 4

mapper.delete:
DELETE FROM T_APPROVAL
 WHERE APPR_NO = #{apprNo}
   AND APPR_STATUS = 4

mapper.update:
UPDATE T_APPROVAL
   SET APPR_TITLE = #{apprTitle}
     , APPR_CONTENT = #{apprContent}
     , MODIFY_DATE = SYSDATE
     , UPDATE_USER = #{updateUser}
 WHERE APPR_NO = #{apprNo}

UPDATE T_DRAFT
   SET ENFORCE_DATE = #{enforceDate}
     , COOP_DEPT = #{coopDept}
 WHERE APPR_NO = #{apprNo}

UPDATE T_PURCH_DRAFT
   SET PURCH_DEPT = #{purchDept}
     , PURCH_EMP_NAME = #{purchEmpName}
     , PURCH_PURPOSE = #{purchPurpose}
     , PURCH_DEADLINE = #{purchDeadline}
     , PURCH_TOTAL = #{purchTotal}
 WHERE APPR_NO = #{apprNo}
