mapper.select:
select EQUIP_NO , EQUIP_NAME , EQUIP_CATEGORY , CREATE_DATETIME , CREATE_USER , UPDATE_DATETIME , UPDATE_USER , STATUS from T_EQUIPMENT where STATUS = 'Y'
and EQUIP_CATEGORY = #{equipment}
order by EQUIP_NO desc

select FACILITY_NO , FACILITY_NAME , FACILITY_CATEGORY , CREATE_DATETIME , CREATE_USER , UPDATE_DATETIME , UPDATE_USER , STATUS from T_FACILITY where STATUS = 'Y'
and FACILITY_CATEGORY = #{facility}
order by FACILITY_NO desc

mapper.update:
update
			 T_FACILITY
	 set FACILITY_NAME = #{name}
		 , FACILITY_CATEGORY = #{selectedCategory}
		 , UPDATE_DATETIME = SYSDATE
		 , UPDATE_USER = #{userNo}
 where FACILITY_NO = #{no}

update
			 T_EQUIPMENT
	 set EQUIP_NAME = #{name}
		 , EQUIP_CATEGORY = #{selectedCategory}
		 , UPDATE_DATETIME = SYSDATE
		 , UPDATE_USER = #{userNo}
 where EQUIP_NO = #{no}
